statements.update:
/* 정수장 상세 수정 */ MERGE INTO INFOFMS.WTL_PURI_AS USING DUAL ON (FTR_CDE = #FTR_CDE# AND FTR_IDN = #FTR_IDN#)
WHEN MATCHED THEN UPDATE SET HJD_CDE = #HJD_CDE# , SHT_NUM = #SHT_NUM# , MNG_CDE = #MNG_CDE# , FNS_YMD = #FNS_YMD# , PUR_NAM = #PUR_NAM# , WSR_CDE = #WSR_CDE# , GAI_NAM = #GAI_NAM# , SRV_NAM = #SRV_NAM# , PUR_VOL = #PUR_VOL# , PWR_VOL = #PWR_VOL# , PUR_ARA = #PUR_ARA# , SAM_CDE = #SAM_CDE# , CNT_NUM = #CNT_NUM# , SYS_CHK = #SYS_CHK#
WHEN NOT MATCHED THEN INSERT ( FTR_CDE , FTR_IDN , HJD_CDE , SHT_NUM , MNG_CDE , FNS_YMD , PUR_NAM , WSR_CDE , GAI_NAM , SRV_NAM , PUR_VOL , PWR_VOL , PUR_ARA , SAM_CDE , CNT_NUM , SYS_CHK ) VALUES ( #FTR_CDE# , #FTR_IDN# , #HJD_CDE# , #SHT_NUM# , #MNG_CDE# , #FNS_YMD# , #PUR_NAM# , #WSR_CDE# , #GAI_NAM# , #SRV_NAM# , #PUR_VOL# , #PWR_VOL# , #PUR_ARA# , #SAM_CDE# , #CNT_NUM# , #SYS_CHK# )

statements.select:
/* 정수장 관리번호 채번 */
SELECT NVL(MAX(FTR_IDN),0)+1 AS FTR_IDN FROM INFOFMS.WTL_PURI_AS
WHERE 1=1
